import { useStore } from 'vuex'
import SockJS from 'sockjs-client/dist/sockjs.min.js'
import Stomp from 'webstomp-client'
// const websocketUrl = `wss://demo.piesocket.com/v3/channel_1?api_key=VCXCEuvhGcBDP7XhiJJUDvR1e1D3eiVjgZ9VRiaV&notify_self`;
// const websocketUrl = `ws://127.0.0.1:8080/`
const store = useStore()
export const websocket = {
  data() {
    return {
      ws: null,
    }
  },
  created() {
    //頁面剛進入時開啟長連線
    this.initWebsocket()
  },
  destroyed: function () {
    //頁面銷燬時關閉長連線
    this.websocketclose()
  },
  methods: {
    //初始websocket
    initWebsocket() {
      this.socket = new SockJS('http://localhost:8080/ws')
      this.stompClient = Stomp.over(this.socket)
      this.stompClient.connect(
        // headers头部信息。可添加客户端的认证信息。也可以不添加信息，headers 直接就设置为 {}
        (header) => {
          header
        },
        (frame) => {
          console.log(frame)
          this.stompClient.subscribe('/topic/bind', function (data) {
            console.log('store' + store)
            if (store) {
              console.log('aaaa' + data)
              this.$store.state.currencyOrder = JSON.parse(data)
            }
            // console.log('ddd' + data)
          })
          this.stompClient.send(
            '/app/testMsg',
            {},
            JSON.stringify({ clientId: 'jjj' }),
          )
          //   this.connectSucceed()
        },
        (err) => {
          this.initWebsocket
          // 连接失败的回调
          console.log(err)
          //   this.reconnect(this.socketUrl, this.connectSucceed)
        },
      )
    },
    websocketonopen() {
      console.log('ws 連線成功~~')
    },
    websocketonerror(e) {
      console.error('ws 連線失敗', e)
    },
    websocketonmessage(e) {
      console.log(e.data)
      store.currentOrder.push(e.data)
      return e
    },
    websocketsend(data, topic = '/submitOrder') {
      this.stompClient.send('/app' + topic, data)
      //前端丟資料
      console.log('send data', data)
    },
    websocketclose() {
      console.log('ws 關閉連線')
    },
  },
}
